INSERT INTO `portafolio` (`id`, `id_entrada`, `title`, `img`, `img1`, `img2`, `img3`, `descort`, `desclarge`, `activo`, `facebook`, `twitter`, `youtube`, `google`) VALUES
(7, 'mario-cano', 'Mario Cano', 'fotos/thumbs/mario-cano.jpg', 'fotos/view/mario-cano.jpg', 'fotos/view/mariocano_portafolio_2.jpg', 'fotos/view/mariocano_portafolio_3.jpg', 'Rediseño de imagen - Fotografía - Diseño web - Redes sociales', 'En Mario cano Accesorios buscamos destacar y promover el diseño mexicano de autor, por lo que realizamos toda una campaña en fotografía, redes sociales y página web enfocado en la ¿hechura? del fino arte joyero.', 1, 'https://www.facebook.com/MarioCanoAccesorios/?fref=ts', '', '', ''),
(8, 'aloretti', 'Aloretti', 'fotos/thumbs/aloretti.jpg', 'fotos/view/aloretti.jpg', 'fotos/view/aloretti_1.jpg', 'fotos/view/aloretti_2.jpg', 'IDENTIDAD GRÁFICA - DISEÑO WEB', 'Aloretti es un concepto de muebles de madera de alta gama, garantizando la originalidad y calidad en cada una de sus piezas. Para Aloretti desarrollamos su identidad gráfica, señaletica vial, y sitio web. Buscamos capturar su aire moderno, minimalista y la alta calidad de sus acabado en su diseño co', 1, '', '', '', ''),
(9, 'sistepack', 'Sistepack', 'fotos/thumbs/sistepack.jpg', 'fotos/view/sistepack.jpg', 'fotos/view/sistepack_portafolio_2.jpg', 'fotos/view/sistepack_portafolio_3.jpg', 'DISEÑO WEB - REDES SOCIALES', 'Con más de 20 años en el mercado,  Sistepack se ha posicionado como uno de los distribuidores más importantes en el sector indutrial, manejando marcas de calidad europea y americana en empaques plásticos. Para este proyecto desarrollamos su nuevo sitio web y su plan de redes sociales.', 1, 'https://www.facebook.com/sistepack/?fref=ts', '', '', ''),
(10, 'anttegra', 'Anttegra', 'fotos/thumbs/anttegra.jpg', 'fotos/view/anttegra.jpg', 'fotos/view/anttegra_1.jpg', 'fotos/view/anttegra_2.jpg', 'IDENTIDAD GRÁFICA - DISEÑO WEB - REDES SOCIALES', 'Anttegra nuestra empresa hermana dedicada a distribuir productos de alta gama en arquitectura y construcción. Para ella, desarrollamos su identidad gráfica, página web, junto con plan de redes sociales. ', 1, '', '', '', ''),
